<?php

namespace Sociedad\SociedadesBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * SociedadesRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class SociedadesRepository extends EntityRepository
{
    public function sociedadesActivas($id)
    {
        $consulta=$this->getEntityManager()
                    ->createQuery('SELECT s FROM  sociedad\sociedadesBundle\Entity\Sociedades s
                                         WHERE s.id!=:id
                                         ORDER BY s.nombre');
        $consulta->setParameter('id', $id);
        $consulta->useResultCache(true, 3600);
        
        return $consulta->getResult();
        
    }    
}
